/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState, memo } from "react";
import { mapTime } from "../mappers/mapTime";
import { getComment } from "../services/hnApi";
import {
	StoryMeta,
	StoryMetaElement,
	StoryWrapper,
} from "../styles/StoryStyles";
// import { HashLink as Link } from "react-router-hash-link";

const Comment = memo(function Comment({ commentId }) {
	const [comment, setComment] = useState();

	useEffect(() => {
		getComment(commentId).then((data) => {
			data && data.text && setComment(data);
		});
	}, []);

	// var output = new Buffer(comment.text, "hex");

	return comment && comment.text ? (
		<StoryWrapper data-testid="comment">
			<StoryMeta>
				<div data-testid="comment-by">
					<StoryMetaElement color="#000"></StoryMetaElement> {comment.by}
				</div>
				<div data-testid="comment-time">
					<StoryMetaElement color="#000"></StoryMetaElement> {}
					{mapTime(comment.time)} ago
				</div>
				<div data-testid="comment-text">
					<StoryMetaElement color="#000">Comment:</StoryMetaElement>
					{new Buffer((comment.text))}
				</div>
			</StoryMeta>
		</StoryWrapper>
	) : null;
});
export default Comment;
