/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from "react";
import { formatDate } from "../mappers/mapTime";
import { clean } from "../mappers/mapTime";
import { getAskText } from "../services/hnApi";
import {
	StoryMeta,
	StoryMetaElement,
	StoryWrapper,
} from "../styles/StoryStyles";
import {
	GlobalStyle,
	StoriesContainerWrapper,
} from "../styles/StoriesContainerStyles";
import { HashLink as Link } from "react-router-hash-link";

export const AskText = ({ storyId }) => {
	const [ask, setAsk] = useState();

	useEffect(() => {
		getAskText(storyId).then((data) => data && setAsk(data));
	}, []);

	return ask ? (
		<>
			<GlobalStyle />
			<StoriesContainerWrapper data-test-id="stories-container">
				&nbsp;
				<h1 style={{ marginTop: "10px" }}>{ask.title}</h1>
				<StoryWrapper data-testid="ask">
					<StoryMeta>
                        <Link to={`/user/${ask.by}`}>
                            <span data-testid="ask-by">
                                <StoryMetaElement color="#000"></StoryMetaElement> {ask.by}
                            </span>
                        </Link>
                        <span data-testid="ask-time">
							<StoryMetaElement color="#000"></StoryMetaElement> {mapTime(story.time)} ago 
						</span>
						<div data-testid="ask-text">
							<StoryMetaElement color="#000"></StoryMetaElement>{" "}
							{ask.text}
						</div>
					</StoryMeta>
				</StoryWrapper>
			</StoriesContainerWrapper>
		</>
	) : (
		<></>
	);
};
