import React, { useEffect, useState } from "react";
import {
	getAskStoryIds,
	getBestStoryIds,
	getJobStoryIds,
	getNewStoryIds,
	getShowStoryIds,
	getTopStoryIds,
} from "../services/hnApi";
import Story from "../components/Story";
import AskStory from "../components/AskStory";
import {
	GlobalStyle,
	StoriesContainerWrapper,
} from "../styles/StoriesContainerStyles";
import { useInfiniteScroll } from "../hooks/useInfiniteScroll";

import {
	TextBlock,
	RectShape,
	TextRow,
} from "react-placeholder/lib/placeholders";
import ReactPlaceholder from "react-placeholder";

const GhostPlaceholder = () => (
	<div
		className="my-placeholder"
		style={{
			marginTop: "100px",
		}}
	>
		{/* <RectShape color="gray" style={{ width: 25, height: 70 }} /> */}
<StoriesContainerWrapper></StoriesContainerWrapper>
		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{ width: "400px" }}
		/>
		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{ width: "300px" }}
		/>
		<hr />

		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{ width: "400px" }}
		/>
		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{ width: "300px" }}
		/>
		<hr />

		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{ width: "400px" }}
		/>
		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{ width: "300px" }}
		/>
		<hr />
	</div>
);

export const NewStoriesContainer = () => {
	const { count } = useInfiniteScroll();
	const [storyIds, setStoryIds] = useState([]);

	useEffect(() => {
		getNewStoryIds().then((data) => setStoryIds(data));
	}, []);

	// if (storyIds.length == 0) {
	// 	return (
	// 		<div style={{ marginTop: "50px" }}>
	// 			&nbsp;
	// 			<div>Looading...</div>
	// 		</div>
	// 	);
	// } else {
	return (
		<ReactPlaceholder
			ready={storyIds.length > 0}
			customPlaceholder={<GhostPlaceholder />}
		>
			<>
				<GlobalStyle />
				<StoriesContainerWrapper data-test-id="stories-container">
					&nbsp;
					{storyIds.slice(0, count).map((storyId) => (
						<Story key={storyId} storyId={storyId} />
					))}
				</StoriesContainerWrapper>
			</>
		</ReactPlaceholder>
	);
	// }
};

export const TopStoriesContainer = () => {
	const { count } = useInfiniteScroll();
	const [storyIds, setStoryIds] = useState([]);

	useEffect(() => {
		getTopStoryIds().then((data) => setStoryIds(data));
	}, []);

	return (
		<>
			<GlobalStyle />
			<StoriesContainerWrapper data-test-id="stories-container">
				&nbsp;
				{storyIds.slice(0, count).map((storyId) => (
					<Story key={storyId} storyId={storyId} />
				))}
			</StoriesContainerWrapper>
		</>
	);
};

export const BestStoriesContainer = () => {
	const { count } = useInfiniteScroll();
	const [storyIds, setStoryIds] = useState([]);

	useEffect(() => {
		getBestStoryIds().then((data) => setStoryIds(data));
	}, []);

	return (
		<>
			<GlobalStyle />
			<StoriesContainerWrapper data-test-id="stories-container">
				&nbsp;
				{storyIds.slice(0, count).map((storyId) => (
					<Story key={storyId} storyId={storyId} />
				))}
			</StoriesContainerWrapper>
		</>
	);
};

export const AskStoriesContainer = () => {
	const { count } = useInfiniteScroll();
	const [storyIds, setStoryIds] = useState([]);

	useEffect(() => {
		getAskStoryIds().then((data) => setStoryIds(data));
	}, []);

	return (
		<>
			<GlobalStyle />
			<StoriesContainerWrapper data-test-id="stories-container">
				&nbsp;
				{storyIds.slice(0, count).map((storyId) => (
					<AskStory key={storyId} storyId={storyId} />
				))}
			</StoriesContainerWrapper>
		</>
	);
};

export const ShowStoriesContainer = () => {
	const { count } = useInfiniteScroll();
	const [storyIds, setStoryIds] = useState([]);

	useEffect(() => {
		getShowStoryIds().then((data) => setStoryIds(data));
	}, []);

	return (
		<>
			<GlobalStyle />
			<StoriesContainerWrapper data-test-id="stories-container">
				&nbsp;
				{storyIds.slice(0, count).map((storyId) => (
					<Story key={storyId} storyId={storyId} />
				))}
			</StoriesContainerWrapper>
		</>
	);
};

export const JobStoriesContainer = () => {
	const { count } = useInfiniteScroll();
	const [storyIds, setStoryIds] = useState([]);

	useEffect(() => {
		getJobStoryIds().then((data) => setStoryIds(data));
	}, []);

	return (
		<>
			<GlobalStyle />
			<StoriesContainerWrapper data-test-id="stories-container">
				&nbsp;
				{storyIds.slice(0, count).map((storyId) => (
					<Story key={storyId} storyId={storyId} />
				))}
			</StoriesContainerWrapper>
		</>
	);
};
