/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from "react";
import { mapTime } from "../mappers/mapTime";
import { getUser } from "../services/hnApi";
import {
	StoryMeta,
	StoryMetaElement,
	StoryWrapper,
} from "../styles/StoryStyles";

export const User = (() => {
	const [story, setStory] = useState(this.props.match.params.userId);
	const [user, setUser] = useState();

	useEffect(() => {
		getUser(story.by).then((data) => data && setUser(data));
	}, []);

	return user ? (
		<StoryWrapper data-testid="user">
			<StoryMeta>
				<span data-testid="user-by">
					<StoryMetaElement color="#000">User:</StoryMetaElement> {user.id}
				</span>
				<span data-testid="user-created">
					<StoryMetaElement color="#000">Created:</StoryMetaElement> {}
					{mapTime(user.created)} ago
				</span>
				<span data-testid="user-karma">
					<StoryMetaElement color="#000">Karma:</StoryMetaElement> {user.karma}
				</span>
				<span data-testid="user-about">
					<StoryMetaElement color="#000">About:</StoryMetaElement> {user.about}
				</span>
				<span data-testid="user-submitted">
					<StoryMetaElement color="#000">Submitted:</StoryMetaElement>{" "}
					{user.submitted}
				</span>
			</StoryMeta>
		</StoryWrapper>
	) : null;
});
