import React, { useEffect, useState } from "react";
import {
	getAskStoryIds,
	getBestStoryIds,
	getJobStoryIds,
	getNewStoryIds,
	getShowStoryIds,
	getTopStoryIds,
} from "../services/hnApi";
import Story from "../components/Story";
import AskStory from "../components/AskStory";
import {
	GlobalStyle,
	StoriesContainerWrapper,
} from "../styles/StoriesContainerStyles";
import { useInfiniteScroll } from "../hooks/useInfiniteScroll";
import {
	TextBlock,
	RectShape,
	TextRow,
} from "react-placeholder/lib/placeholders";
import ReactPlaceholder from "react-placeholder";

const GhostPlaceholder = () => (
	<div
		className="my-placeholder"
		style={{
			marginTop: "50px",
		}}
	>
		{/* Skeleton */}
		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{
				width: "400px",
				padding: "0px, 20px",
				marginLeft: "100px",
				marginBottom: "20px",
			}}
		/>
		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{
				width: "300px",
				padding: "0px, 20px",
				marginLeft: "120px",
				marginBottom: "20px",
			}}
		/>
		<section
			style={{
				width: "1140px",
				borderTop: "1px solid #cccccc",
				marginLeft: "100px",
			}}
		/>

		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{
				width: "400px",
				padding: "0px, 20px",
				marginLeft: "100px",
				marginBottom: "20px",
			}}
		/>
		<TextRow
			rows={6}
			color="rgba(242, 242, 242, 1)"
			style={{
				width: "300px",
				padding: "0px, 20px",
				marginLeft: "120px",
				marginBottom: "20px",
			}}
		/>
		<hr style={{ width: "1140px", borderTop: "1px solid #cccccc" }} />

		<TextRow
