import React, { useEffect, useState } from "react";
import { getCommentIds, getStory } from "../services/hnApi";
import Comment from "../components/Comment";
import {
	GlobalStyle,
	StoriesContainerWrapper,
} from "../styles/StoriesContainerStyles";
import { useInfiniteScroll } from "../hooks/useInfiniteScroll";
import { StoryTitle } from "../styles/StoryStyles";

export const CommentContainer = ({ storyId }) => {
	const { count } = useInfiniteScroll();
	const [commentIds, setCommentIds] = useState([]);
	const [story, setStory] = useState();

	useEffect(() => {
		getStory(storyId).then((data) => {
			data && data.url && setStory(data);
		});
	}, []);

	console.log(storyId);

	// useEffect(() => {
	// 	getCommentIds(storyId).then((data) => setCommentIds(data));
	// }, []);

	return (
		<>
			<GlobalStyle />
			<StoriesContainerWrapper data-test-id="stories-container">
				<>&nbsp;</>
				<StoryTitle>
					<a href={story.url}>
						<p>{story.title}</p>
					</a>
				</StoryTitle>
				<h1 style={{ marginTop: "10px", fontStyle: "normal"}}><b>Comments</b></h1>
				{commentIds.slice(0, count).map((commentId) => (
					<Comment key={commentId} commentId={commentId} />
				))}
			</StoriesContainerWrapper>
		</>
	);
};
