/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState, memo } from "react";
import { mapTime, check } from "../mappers/mapTime";
import { getComment } from "../services/hnApi";
import {
	StoryMeta,
	StoryMetaElement,
	StoryTitle,
	StoryWrapper,
} from "../styles/StoryStyles";
import { HashLink as Link } from "react-router-hash-link";

const Comment = memo(function Comment({ commentId }) {
	const [comment, setComment] = useState();

	useEffect(() => {
		getComment(commentId).then((data) => {
			data && data.text && setComment(data);
		});
	}, []);

	return story && story.title ? (
		<StoryWrapper data-testid="story">
			<StoryTitle>
				<a href={story.url}>
					<p>{story.title}</p>
				</a>
			</StoryTitle>
			<StoryMeta>
				<span data-testid="story-score">
					<StoryMetaElement color="#000">Points:</StoryMetaElement>{` `}
					{story.score}
				</span>
				<Link to={`/user/${story.by}`}>
					<span data-testid="story-by">
						<StoryMetaElement color="#000">By:</StoryMetaElement> {` `}
						{story.by} &nbsp;
					</span>
				</Link>
				<span data-testid="story-time">
					<StoryMetaElement color="#000">Posted:</StoryMetaElement> {` `}
					{mapTime(story.time)} ago &nbsp;
				</span>
				<span data-testid="story-comments">
					<StoryMetaElement color="#000">Comments:</StoryMetaElement>{` `}
					{check(story.kids)}
				</span>
			</StoryMeta>
		</StoryWrapper>
	) : (
		<></>
	);
});
export default Story;
