import React from "react";
import { CommentContainer } from "../containers/CommentContainer";
import { useParams } from "react-router-dom";
import { Layout, Menu, Breadcrumb } from "antd";
import { HashLink as Link } from "react-router-hash-link";
import Logo from "../images/hacker-news1_logo-c6b9933e1bd8db44697993fd5cf2386ae9cab6d650276e1d0769fdceee1502aa.jpg";
import { getStory } from "../services/hnApi";

function CommentPage() {
	let { storyId } = useParams();
	const { Header, Content, Footer } = Layout;
	const [story, setStory] = useState();

	useEffect(() => {
		getStory(storyId).then((data) => data && setStory(data));
	}, []);

	return (
		<Layout className="layout">
			<Header
				style={{
					position: "fixed",
					zIndex: 1,
					width: "100%",
					background: "rgb(255, 102, 0)",
				}}
			>
				<div className="logo">
					<img className="hackernews-logo" src={Logo} />
				</div>
				<Menu
					theme="dark"
					mode="horizontal"
					style={{ background: "rgb(255, 102, 0)" }}
				>
					<Menu.Item key="1">
						<Link to="/">New</Link>
					</Menu.Item>
					<Menu.Item key="2">
						<Link to="/top">Top</Link>
					</Menu.Item>
					<Menu.Item key="3">
						<Link to="/best-stories">Best Stories</Link>
					</Menu.Item>
					<Menu.Item key="4">
						<Link to="/ask">Ask</Link>
					</Menu.Item>
					<Menu.Item key="5">
						<Link to="/show">Show</Link>
					</Menu.Item>
					<Menu.Item key="6">
						<Link to="/jobs">Jobs</Link>
					</Menu.Item>
				</Menu>
			</Header>
			<StoryTitle>
				<a href={story.url}>
					<p>{story.title}</p>
				</a>
			</StoryTitle>
			<Content style={{ padding: "0 50px" }}>
				<div className="site-layout-content">
					<CommentContainer storyId={storyId} />
				</div>
			</Content>
			<Footer style={{ textAlign: "center" }}>Hello ©2020 Created by Us</Footer>
		</Layout>
	);
}

export default CommentPage;
