import React, { useEffect, useState } from "react";
import { formatDate } from "../mappers/mapTime";
import {clean} from "../mappers/mapTime";
import { getUser } from "../services/hnApi";
import {
	StoryMeta,
	StoryMetaElement,
	StoryWrapper,
} from "../styles/StoryStyles";
import { HashLink as Link } from "react-router-hash-link";

export const User = ({ userId }) => {
	const [user, setUser] = useState();

	useEffect(() => {
		getUser(userId).then((data) => data && setUser(data));
	}, []);

	console.log(user);

	return user ? (
		<StoryWrapper data-testid="user">
			<StoryMeta>
				<div data-testid="user-by">
					<StoryMetaElement color="#000">User: </StoryMetaElement> {user.id}
				</div>
				<div data-testid="user-created">
					<StoryMetaElement color="#000">Joined: </StoryMetaElement> {}
					{formatDate(user.created)}
				</div>
				<div data-testid="user-karma">
					<StoryMetaElement color="#000">Karma: </StoryMetaElement> {user.karma}
				</div>
				<div data-testid="user-about">
					<StoryMetaElement color="#000">About: </StoryMetaElement>{" "}
					{clean(user.about)}
				</div>
				<Link to={`/user/${story.by}/submission`}>
					<div data-testid="user-submitted">
						<StoryMetaElement color="#000">Submission </StoryMetaElement>{" "}
						{/* {user.submitted} */}
					</div>
				</Link>
			</StoryMeta>
		</StoryWrapper>
	) : null;
};
