/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState, memo } from "react";
import { mapTime, check } from "../mappers/mapTime";
import { getComment } from "../services/hnApi";
import {
	StoryMeta,
	StoryMetaElement,
	StoryTitle,
	StoryWrapper,
} from "../styles/StoryStyles";
import { HashLink as Link } from "react-router-hash-link";

const Comment = memo(function Comment({ commentId }) {
	const [comment, setComment] = useState();

	useEffect(() => {
		getComment(commentId).then((data) => {
			data && data.text && setComment(data);
		});
	}, []);

	return (comment && comment.text ? (
		<StoryWrapper data-testid="story">
			<StoryMeta>
				<div data-testid="user-by">
					<StoryMetaElement color="#000">User:</StoryMetaElement> {user.id}
				</div>
				<div data-testid="user-created">
					<StoryMetaElement color="#000">Joined:</StoryMetaElement> {}
					{formatDate(user.created)}
				</div>
				<div data-testid="user-karma">
					<StoryMetaElement color="#000">Karma:</StoryMetaElement> {user.karma}
				</div>
				<div data-testid="user-about">
					<StoryMetaElement color="#000">About:</StoryMetaElement> {user.about}
				</div>
				<div data-testid="user-submitted">
					<StoryMetaElement color="#000">Submitted:</StoryMetaElement>{" "}
					{user.submitted}
				</div>
			</StoryMeta>
		</StoryWrapper>
	) : null);
});
export default Comment;
