import React from "react";
import { NewStoriesContainer } from "../containers/StoriesContainer";

import { Layout, Menu, Breadcrumb } from "antd";
import { HashLink as Link } from "react-router-hash-link";

function HeaderFooter() {
	const { Header, Content, Footer } = Layout;
	return (
		<Layout className="layout">
			<Header
				style={{
					position: "fixed",
					zIndex: 1,
					width: "100%",
					background: "rgb(255, 102, 0)",
					backgroundImage: ""
				}}
			>
				{/* <div className="logo"/> */}

				<Menu
					theme="dark"
					mode="horizontal"
					defaultSelectedKeys={["1"]}
					style={{ background: "rgb(255, 102, 0)" }}
				>
					<Menu.Item key="1" style={{ color: "red" }} className="menu-block">
						<Link to="/">New</Link>
					</Menu.Item>
					<Menu.Item key="2">
						<Link to="/top">Top</Link>
					</Menu.Item>
					<Menu.Item key="3">
						<Link to="/best-stories">Best Stories</Link>
					</Menu.Item>
					<Menu.Item key="4">
						<Link to="/ask">Ask</Link>
					</Menu.Item>
					<Menu.Item key="5">
						<Link to="/show">Show</Link>
					</Menu.Item>
					<Menu.Item key="6">
						<Link to="/jobs">Jobs</Link>
					</Menu.Item>
				</Menu>
			</Header>
			<Content style={{ padding: "0 50px" }}>
				<div className="site-layout-content">
					<NewStoriesContainer />
				</div>
			</Content>
			<Footer style={{ textAlign: "center" }}> &copy;2020</Footer>
		</Layout>
	);
}

export default HeaderFooter;
